separator"></div>	<div class="section">		<div class="left">			<p class="label">Start Date</p>		</div>		<div class="right">			<p><?php echo $client->client_start_date; ?></p>		</div>	</div>	<div class="border_separator"></div>	<div class="section">		<div class="left">			<p class="label">Next Schedule Maintenance</p>		</div>		<div class="right">			<p><?php echo $client->client_next_schedule_maintenance; ?></p>		</div>	</div>	<div class="border_separator"></div>	<div class="section">		<div class="left">			<p class="label">Hourly Rate</p>		</div>		<div class="right">			<p><?php echo $client->client_hourly_rate; ?></p>		</div>	</div>	<div class="border_separator"></div>	<div class="section">		<div class="left">			<p class="label">Internet Service Provider</p>		</div>		<div class="right">			<p><?php echo $client->client_internet_service_provider; ?></p>		</div>	</div>	<div class="border_separator"></div>	<div class="section">		<div class="left">			<p class="label">Phone Company</p>		</div>		<div class="right">			<p><?php echo $client->client_phone_company; ?></p>		</div>	</div>	<div class="border_separator"></div>	<div class="section">		<div class="left">			<p class="label">External IP Numbers</p>		</div>		<div class="right">			<p><?php echo $client->client_external_ip_numbers; ?></p>		</div>	</div>	<div class="border_separator"></div>	<div class="section">		<div class="left">			<p class="label">External DNS Name</p>		</div>		<div class="right">			<p><?php echo $client->client_external_dns_name; ?></p>		</div>	</div>	<div class="border_separator"></div>	<div class="section">		<div class="left">			<p class="label">Remote Connection</p>		</div>		<div class="right">			<p>				<?php 					$client_remote_connection = unserialize($client->client_remote_connection); 					echo implode(', ', $client_remote_connection);				?>			</p>		</div>	</div>	<div class="border_separator"></div>	<div class="section">		<div class="left">			<p class="label">Accounts Info</p>		</div>		<div class="right">			<p><?php echo $client->client_accounts_info; ?></p>		</div>	</div>	<div class="border_separator"></div>	<div class="section">		<div class="left">			<p class="label">Notes</p>		</div>		<div class="right">			<p><?php echo $client->client_notes; ?></p>		</div>	</div>	<div class="border_separator"></div>	<div class="section">		<div class="left">			<p class="label">Asset</p>		</div>		<div class="asset_container">					<?php 			$client_asset = stripslashes($client->client_asset);				$json_decode = json_decode('[' .$client_asset. ']');			$counter = 1;						$group_assets_array = array();			foreach($json_decode as $key => $assets){				foreach($assets as $asset_type_counter => $asset){					$asset_type = $asset->asset_type;					$asset_type = str_replace('asset_', "", $asset_type);					$group_assets_array[$asset_type][] = $assets;				}			}									foreach($group_assets_array as $asset_type => $json_decode){	?>			<div class="asset_separator asset_group">				<p class="asset_title label"><?php echo ucwords($asset_type); ?></p>	<?php		 foreach($json_decode as $key => $assets){ ?>					<ul class="asset_groups">	<?php					foreach($assets as $asset_type_counter => $asset){						foreach($asset as $input_title => $input_value){							$title_filter = str_replace($asset_type .'_', "", $input_title);							$title = str_replace('_', " ", $title_filter);							if($input_title != 'asset_type_counter' && $input_title != 'asset_type'){			?>								<li>									<p class="label"><?php echo ucwords($title); ?></p>									<p class="input_value"><?php echo (is_array($input_value) ? implode(', ', $input_value) : $input_value); ?></p>								</li>			<?php									}						}					}		?>					</ul>		<?php				$counter++;				}				echo '</div>';			}		?>					</div>	</div>	<a class="button_2 display_button" href="/client/">Return</a>	<a id="create_clients" class="button_1 display_button padding_button" href="/add-client/">+ Add Client</a>	<a class="button_2 display_button" href="/edit-client/?editID=<?php echo $client->ID ?>">Edit</a>	<a class="button_2 display_button confirm" href="/client/?deleteID=<?php echo $client->ID ?>">Delete</a></div><?php get_footer(); ?>